mapper.select:
SELECT * FROM express WHERE statusid=1 AND deadtime>#{nowDate}
AND buildingid=#{buildingId}
LIMIT ${express_lastId},10
LIMIT 0,10

SELECT * FROM express WHERE getuseropenid=#{getUserOpenId}
AND statusid=2 AND (deadtime>#{nowDate} OR deadtime=#{nowDate})
AND statusid=3
AND statusid=2 AND deadtime<#{nowDate}
ORDER BY gettime DESC

mapper.update:
UPDATE
    express
SET
    senduserpay=#{sendUserPay}
WHERE
    id=#{id}

UPDATE
    express
SET
    sendusercancle=#{sendUserCancle}
WHERE
    id=#{expressId}

UPDATE
    express
SET
    getusercomplete=#{getUserComplete}
WHERE
    id=#{expressId}
